import React, { useState, useEffect } from 'react';
//* Input module is being used to record the users input
import Input from './components/Input';
//*CountriesList module is being used to display the countries
import CountriesList from './components/CountriesList';
//*countriesServices module is being used to get the axios data 
import countriesServices from './services/countriesServices';

//*App is being used to exported and imported to index.js so it can be rendered
const App = () => {
  //! the useState's initialValue is an empty array, the state is countries, and the function is setCountries 
  const [countries, setCountries] = useState([]);
  //! the useState's initialValue is an empty string, the state is filter, and the function is setFilter
  const [filter, setFilter] = useState('');
  //*useEffect will be used to fetch the data that will be set into setCountries 
  useEffect(() => {
    countriesServices.getAll(setCountries)
  }, []);

  //*handleFilter is a function that has a parameter of e, e is the value of the user input. This value is then saved in setFilter.
  const handleFilter = (e) => {
    setFilter(e.target.value)
    //console.log(e.target.value)
  }

  return (
    <div>
      <Input handleFilter={handleFilter} filterValue={filter}/>
      <CountriesList countries={countries} filter={filter} setFilter={setFilter}/>
    </div>
  );
};

export default App;
