import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Filter({ handleFilterChange }) {
  return (
    <input type="text" onChange={(e) => handleFilterChange(e.target.value)} />
  );
}

function Countries({ countries }) {
  return (
    <ul>
      {countries.map((country) => (
        <li key={country}>{country}</li>
      ))}
    </ul>
  );
}

function App() {
  const [countries, setCountries] = useState([]);
  const [filter, setFilter] = useState('');

  useEffect(() => {
    axios
      .get('https://restcountries.com/v2/all')
      .then((res) => {
        const countries = res.data.map((country) => country.name);
        setCountries(countries);
      })
      .catch((err) => console.error(err));
  }, []);

  const handleFilterChange = (filter) => {
    setFilter(filter);
  };

  const filteredCountries = countries
    .filter((country) => country.toLowerCase().includes(filter.toLowerCase()))
    .sort();

  return (
    <div>
      <Filter handleFilterChange={handleFilterChange} />
      <Countries countries={filteredCountries} />
    </div>
  );
}

export default App;



