import { useState, useRef, useEffect } from 'react'
//needed to call functions of axios
import countriesServices from './services/countries'
//needed to let the filter system work
import Filter from './components/Filter';
//needed to create a list of objects after filtered
import List from './components/List';


const App = () => {
  const [countries,setCountries] = useState([])
  const [newFilter,setNewFilter] = useState('')

  useEffect(()=>{
    //console.log('getting api for countries')
    countriesServices.getAll()
    .then(response =>{
      // console.log('successfully reached countries api')
      let sorted = response.data.slice().sort((a, b) => {
        if (a.name.common < b.name.common) return -1;
        if (a.name.common > b.name.common) return 1;
        return 0;
      })
      setCountries(sorted)

      // let sorted = response.data.sort(function (a, b) {
      //   if (a.name < b.name) {
      //     return -1;
      //   }
      //   if (a.name > b.name) {
      //     return 1;
      //   }
      //   return 0;
      // });
      // setCountries(sorted)
    })
  })

  const handleFilter = (e) => setNewFilter(e.target.value)

  return(
    <>
      <Filter handleFilter={handleFilter} value={newFilter}/>
      <List countries={countries} filter={newFilter} setFilter={setNewFilter}/>
    </>
  )
}

export default App
