import { useState, useRef, useEffect } from 'react'
import Filter from './components/Filter';
import axios from 'axios';

const App = () => {
  const [countries,setCountries] = useState([])
  const [newFilter,setNewFilter] = useState('')

  useEffect(()=>{
    console.log('getting api for countries')
    axios.get('https://restcountries.com/v3.1/all')
    .then(response =>{
      console.log('successfully reached countries api')
      let sorted = response.data.sort((a,b)=>{
        if(a.name < b.name)
        {
          return -1
        }
        if(a.name > b.name)
        {
          return 1
        }
        return 0
      })
      setCountries(sorted)
    })
  })

  const handleFilter = (e) => setNewFilter(e.target.value)

  return(
    <>
      <Filter handleFilter={handleFilter} value={newFilter}/>
    </>
  )
}

export default App
