import { useState } from 'react'
import Note from './components/Note'


const App = (props) => {

  const [notes, setNotes] = useState(props.notes)
  const [newNote, setNewNote] = useState(    'a new note...'  ) 

  console.log(props.notes)

  const addNote = (event) => {
    event.preventDefault()
    const noteObject = {
      content: newNote,
      date: new Date().toISOString(),
      important: Math.random() < 0.5,
      id: notes.length + 1,
    }

    setNotes(notes.concat(noteObject))
    setNewNote('')
  }

  const handleNoteChange = (event)=>{
    console.log(event.target.value)
    setNewNote(event.target.value)
  }

  return (
    <div>
      <h1>Notes</h1>
      <ul>
        {notes.map(note => 
          <Note key={note.id} note={note} />
        )}
      </ul>
      <form onSubmit={addNote}>
        <input value={newNote} onChange={handleNoteChange}/>        
        <button type="submit">save</button>
      </form>   
    </div>
  )
}

export default App

//steps of how this works 
/*
1) you create a form that calls addNote function 
2) addNote function first prevents the form from refreshing using the preventDefault
3) in addNote a noteobject is created that is holding the value of newNote and creating an id based on the original array from Notes
*/
