import { useState, useEffect } from 'react'
import axios from 'axios';

import Filter from './components/Filter'
import Form from './components/Form'
import Persons from './components/Persons'


const App = () => {

  const [persons, setPersons] = useState([])


  /*
  testing useEffect
  */
 useEffect(()=>{
  //axios is being used to grab the data from the localhost server holding persons data
  axios.get('http://localhost:3001/persons')
  //if found it will skim through each item
  .then(response =>{
    //the values from response.data each time its returned is being sent to setPersons
    setPersons(response.data)
  })
 })


  //search filter
  const [newFilter, setNewFilter] = useState('')
  //new person
  const [newName, setNewName] = useState('')
  //phone numbers
  const [newPhoneNumber, setNewPhoneNumber] = useState('')

  //handlers

  const handleFilter = (e)=> setNewFilter(e.target.value)

  const handleSubmit = (e)=>{
    e.preventDefault()

    const nameObject = 
    {
      name:newName,
      number:newPhoneNumber,
      id: persons.length+1
    }  

    if(persons.map((p)=>p.name).includes(newName))
    {
      alert(`${newName} is already added to phonebook`)
    }
    else
    {
      alert('ok')
      setPersons(persons.concat(nameObject))
      setNewName('')
      setNewPhoneNumber('')
    }

  }

  const handleNameInput = (e)=>{
    //e.target.value is the value being put into the form
    setNewName(e.target.value)
  }

  const handleNumInput = (e)=>{
    //e.target.value is the value being put into the form
    setNewPhoneNumber(e.target.value)
  }

    //end of handlers

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter handleFilter={handleFilter} value={newFilter}/>
      <h3>Add a new</h3>
      <Form 
        handleSubmit={handleSubmit} 
        handleNameInput={handleNameInput} 
        newName={newName}
        handleNumInput={handleNumInput}
        newPhoneNumber={newPhoneNumber}
      />
      <h3>Numbers</h3>
      <Persons persons={persons} newFilter={newFilter}/>

    </div>
  )
}

export default App

