import { useState } from 'react'
import Filter from './components/Filter'
import Form from './components/Form'
import Persons from './components/Persons'


const App = () => {

  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])

  const [newFilter, setNewFilter] = useState('')
  //new person
  const [newName, setNewName] = useState('')
  //phone numbers
  const [newPhoneNumber, setNewPhoneNumber] = useState('')

  //handlers

  const handleFilter = (e)=>{
    //used to prevent page refresh
    e.preventDefault()

    //search is from the value of input (e), it is then sent to filterPeople
    const search = e.target.value.toLowerCase()
    //this is looking for each name inside of persons letter by letter that is inlcude from search, if found it will appear
    const filteredNames = persons.filter(names => names.name.toLowerCase().includes(search))
    //this is then showing all the people with the filteredNames
    setPersons(filteredNames)

  }
  const handleSubmit = (e)=>{
    e.preventDefault()

    const nameObject = 
    {
      name:newName,
      number:newPhoneNumber,
      id: persons.length+1
    }  

    if(persons.map((p)=>p.name).includes(newName))
    {
      alert(`${newName} is already added to phonebook`)
    }
    else
    {
      alert('ok')
      setPersons(persons.concat(nameObject))
      setNewName('')
      setNewPhoneNumber('')
    }

  }

  const handleNameInput = (e)=>{
    //e.target.value is the value being put into the form
    setNewName(e.target.value)
  }

  const handleNumInput = (e)=>{
    //e.target.value is the value being put into the form
    setNewPhoneNumber(e.target.value)
  }

    //end of handlers

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter handleFilter={handleFilter} value={newFilter}/>
      <h3>Add a new</h3>
      <Form 
        handleSubmit={handleSubmit} 
        handleNameInput={handleNameInput} 
        newName={newName}
        handleNumInput={handleNumInput}
        newPhoneNumber={newPhoneNumber}
      />
      <h3>Numbers</h3>
      <Persons persons={persons}/>

    </div>
  )
}

export default App

