import { useState } from 'react'

const App = () => {
  const [persons, setPersons] = useState([{ name: 'Arto Hellas' }]) 
  const [newName, setNewName] = useState('')
  //phone numbers
  const [newPhoneNumber, setNewPhoneNumber] = useState('')

  //console.log(persons.length)

  const addInfo = (e)=>{

    e.preventDefault()

    const nameObject = {
      name:newName,
      id: persons.length+1
    }

    const phoneNumberObject = {
      number:newPhoneNumber
    }

    //check if a person name is already in the system
    if(persons.map((p)=>p.name).includes(newName))
    {
      alert(`${newName} is already added to phonebook`)
    }
    else{
      alert('ok')
      setPersons(persons.concat(nameObject))
      setNewName('')
    }

    //check if a persons number is already in the system
    if(phoneNumbers.map((p)=>p.number).includes(newPhoneNumber))
    {
      alert(`${newPhoneNumber} is already added to phonebook`)
    }
    else{
      alert('ok')
      setPersons(phoneNumbers.concat(newPhoneNumber))
      setNewName('')
    }


    //console.log("Button Clicked", e.target, nameObject)
  }

  const handleNameChange = (e)=>{
    //console.log(e.target.value)
    setNewName(e.target.value)
  }

  //changing form numbers
  const handleNumberChange = (e)=>{
    setNewPhoneNumber(e.target.value)
  }

  const Person = (props)=>{
    return(
      <h3>{props.name}</h3>
    )

  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addInfo}>
        <div>name: <input value={newName} onChange={handleNameChange}/></div>
        <div>number: <input value={newPhoneNumber} onChange={handleNumberChange}/></div>
        <div><button type="submit">add</button></div>
      </form>

      <h2>Numbers</h2>
      {persons.map(p=> <Person key={p.name} name={p.name} number={p.number}/>)}
    </div>
  )
}



export default App

